import React, { useState } from 'react';
import '../contact.css'
import '../../css/font-awesome.css'
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from 'react';
import { contactAction_get } from '../../store/contactReducer';
import ContactItem from './ContactItem'
import { Link, Navigate } from 'react-router-dom';
import Search from './Search';

const ContactList = (props) => {

	const contactsList = useSelector(state => state.contactsList);
	const dispatch = useDispatch();
	const [searchTxt, set_searchTxt] = useState('');
	

	useEffect(() => {
		dispatch(contactAction_get());
	}, [])

	return (
		<>
			<div className="contact-container">

				<Search searchTxt={searchTxt} set_searchTxt={set_searchTxt}/>
				

				<div className="contacts-container">
					{
						contactsList.filter(x=>x.name.toLowerCase().includes(searchTxt.toLowerCase())||x.phone.toLowerCase().includes(searchTxt.toLowerCase())).map((item, i)=>
						<ContactItem item={item} i={i} key={item.id}/>
						)		
					}
				</div>

				
				<div className="contact-new">
					<Link to='/contacts/new'>
						<button >
						<i className="fa fa-user-plus" aria-hidden="true"></i>
					</button>
					</Link>
					
					<button>
						<i className="fa fa-random" aria-hidden="true" style={{ marginLeft: '15px' }}></i>
					</button>
				</div>

			</div>
		</>
	);
};

export default ContactList;
