import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useLocation, useParams } from 'react-router-dom';
import contactService from '../../services/contactService';
import { contactAction_edit, contactAction_post } from '../../store/contactReducer';
import '../contact.css'
const EditContact = (props) => {
    const location=useLocation();
    const [item, setItem] = useState(location.state||{name:'',phone:'',title:'',imageUrl:contactService.getPictureId()});
    const {id}=useParams();
    const dispatch=useDispatch();
    
    const save = async () => {
        if(id)
        dispatch(contactAction_edit(item));
        else
        dispatch(contactAction_post(item));
    }

    const updateFormState = (event) => {
        setItem({
            ...item,
            [event.target.name]: event.target.value,
        });
    };

   const refreshImage=async()=>{
        let newUrl=await contactService.getPictureId();
        setItem({...item,imageUrl:newUrl})
    }

    return (

		<div className="contact-container">
            <div className="new-contact-container">
                <div className='new-contact-avatar'>
                        <img src={contactService.pictureUrl(item.imageUrl)}/>
                        <button onClick={refreshImage}><i className="fa fa-refresh" aria-hidden="true"></i></button>
                </div>
                <div className="new-contact-inputs">
                    <div className="new-contact-input">
                        <label>Name</label>
                        <input name='name' value={item.name} onChange={updateFormState} />
                    </div>
                    <div className="new-contact-input">
                        <label>Phone</label>
                        <input name='phone' value={item.phone} onChange={updateFormState}/>
                    </div>
                    <div className="new-contact-input">
                        <label name='title' value={item.title} onChange={updateFormState}>Title</label>
                        <input/>
                    </div>
                </div>
                <div className="new-contact-buttons">
                    <button className="button-ok" onClick={save}>Save</button>
                    <button className="button-cancel">Cancel</button>
                </div>
            </div>
        </div>

    );
};

export default EditContact;
