import React from 'react';
import '../contact.css'
import '../../css/font-awesome.css'
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from 'react';
import { contactAction_get } from '../../store/contactReducer';
import contactService from '../../services/contactService';


const ContactList = (props) => {

	const contactsList = useSelector(state => state.contactsList);
	const dispatch = useDispatch();

	useEffect(() => {
		dispatch(contactAction_get(contactsList));

	}, [])

	return (
		<>
			<div className="contact-container">

				{/* searc */}
				<div className="search-input">
					<input type="text" placeholder="search in contacts..." />
					<div className="search-icon">
						<i className="fa fa-search" aria-hidden="true"></i>
					</div>
				</div>

				<div className="contacts-container">
					{
						contactsList.map((item, i)=>
						<div className="contact" key={item.id}>
							<div className="contact-avatar">
								<img src={contactService.pictureUrl(item.imageId,item.title)} />
							</div>
							<div className="contact-details">
								<div className="contact-name">{item.name}</div>
								<div className="contact-phone">{item.phon}</div>
							</div>
							<div className="contact-buttons">
								<button><i className="fa fa-phone" aria-hidden="true"></i></button>
							</div>
							<div className="contact-button-close">
								<i className="fa fa-times" aria-hidden="true"></i>
							</div>
						</div>)		
					}
				</div>
				<div className="contact-new">
					<button>
						<i className="fa fa-user-plus" aria-hidden="true"></i>
					</button>
					<button>
						<i className="fa fa-random" aria-hidden="true" style={{ marginLeft: '15px' }}></i>
					</button>
				</div>

			</div>
		</>
	);
};

export default ContactList;
