import React, { useState } from 'react';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import contactService from '../services/contactService';
import { contactAction_edit, contactAction_post } from '../store/contactReducer';
import './contact.css'
const EditContact = (props) => {
    const location = useLocation();
    const [item, setItem] = useState(location.state || { name: '', phone: '', title: '', imageUrl: '' });
    const { id } = useParams();
    const dispatch = useDispatch();
    const mode = id ? 'edit' : 'new';
    const navigate = useNavigate();


    useEffect(() => {
        if (mode == 'new') {
            refreshImage();
        }
    }, [])



    const save = async () => {
        console.log('save');
        if (mode == 'edit')
            dispatch(contactAction_edit(item, () => { navigate('/contacts') }));
        else {
            dispatch(contactAction_post(item));
            navigate('/contacts')
        }
    }

    const updateFormState = (event) => {
        setItem({
            ...item,
            [event.target.name]: event.target.value,
        });
    };

    const refreshImage = async () => {
        let newUrl = await contactService.genragePictureUrl();
        setItem({ ...item, imageUrl: newUrl })
    }

    return (

        <div className="contact-container">
            <div className="new-contact-container">
                <div className='new-contact-avatar'>
                    <img src={contactService.pictureUrl(item.imageUrl)} />
                    <button onClick={refreshImage}><i className="fa fa-refresh" aria-hidden="true"></i></button>
                </div>
                <div className="new-contact-inputs">
                    <div className="new-contact-input">
                        <label>Name</label>
                        <input name='name' value={item.name} onChange={updateFormState} maxLength="30"/>
                    </div>
                    <div className="new-contact-input">
                        <label>Phone</label>
                        <input name='phone' value={item.phone} onChange={updateFormState} pattern="[0-9]+"
                        onInput={(e)=>{console.log(e)}}/>
                    </div>
                    <div className="new-contact-input">
                        <label>Title</label>
                        <input name='title' value={item.title} onChange={updateFormState} maxLength="100"/>
                    </div>
                </div>
                <div className="new-contact-buttons">
                    <button className="button-ok" onClick={save}>Save</button>
                    <Link to='/contacts'>
                        <button className="button-cancel">Cancel</button>
                    </Link>
                </div>
            </div>
        </div>

    );
};

export default EditContact;
